package leetcode;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

class FindTheDifferenceTest {

    private final FindTheDifference.Solution subject = new FindTheDifference.Solution();

    @Test
    void findTheDifference_returnDifference() {

        // Given
        String input1 = "abcd";
        String input2 = "abcde";

        // When
        char result = subject.findTheDifference(input1, input2);

        // Then
        assertThat(result)
                .isEqualTo('e');
    }

    @Test
    void findTheDifference_complex_returnDifference() {

        // Given
        String input1 = "";
        String input2 = "";

        // When
        char result = subject.findTheDifference(input1, input2);

        // Then
        assertThat(result)
                .isEqualTo('t');
    }
}